    where T1 : class
    where T2 : class
    where T3 : class
{
}

Sample.Before:
foreach (object item in items)
{
    yield return item;
}

Sample.After:
for (int i = items.Count - 1; i >= 0; i--)
{
    yield return items[i];
}

Sample.Before:
if (condition1)
{
    Foo1();

    if (condition2)
    {
        Foo2();

        if (condition3)
        {
            Foo3();
        }
    }
}

Sample.After:
if (!condition1)
{
    return;
}

Foo1();

if (!condition2)
{
    return;
}

Foo2();

if (!condition3)
{
    return;
}

Foo3();

